import React from "react";
import {Link} from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { pokeDetail } from "../actions/index";
import { useEffect } from "react";


export default function Detail(props){
    console.log(props)
    const dispatch=useDispatch()

    useEffect(()=>{
        dispatch(pokeDetail(props.match.params.id));
    },[dispatch])

    const myPoke = useSelector((state)=>state.detail)
console.log("myPoke",myPoke)
    return(
        <div>
            {
            myPoke.length>0 ?
            <div>
                <h1>{myPoke[0].name}</h1>
                <img src={myPoke[0].img}/>
                <h3>ID:{myPoke[0].id}</h3>
                <h4>Stats</h4>
                <h5>Vida:{myPoke[0].hp}</h5>
                <h5>Ataque:{myPoke[0].attack}</h5>
                <h5>Defensa:{myPoke[0].defense}</h5>
                <h5>Velocidad:{myPoke[0].speed}</h5>
                <h6>Altura:{myPoke[0].height}</h6>
                <h6>Peso:{myPoke[0].weight}</h6>
                <h6>Tipos:{myPoke[0].type? myPoke[0].type: myPoke[0].types}</h6>
                
            </div>
            
            :
            console.log("pedo")
            
            }
            <Link to='/home'>
                <button>Volver al </button>
            </Link>

        </div>
    )
}
