import React from "react";
import {useState,useEffect} from "react";
import {useDispatch,useSelector} from 'react-redux'
import { getPokemons } from "../actions";
import { Link } from "react-router-dom";
import Card from "./Card";



export default function Home(){
    const dispatch= useDispatch()

    const allPokemons=useSelector((state)=>state.pokemons)
    useEffect(()=>{
        dispatch(getPokemons());
    },[dispatch])


    function handleClick(e){
        e.preventDefault();
        dispatch(getPokemons());

    }
    return(
<div>
    <Link to='/character'>Create Pokemón</Link>
    <h1>PI Pokemón</h1>
    <button onclick={e=>{handleClick(e)}}>
        Volver a cargar todos los pokemons
    </button>
    <div>
           <select placeholder="Alfabetico">
            <option value='ascABC'>Ascendente</option>
            <option value='descABC'>Descendente</option>

           </select>

           <select placeholder="Ataque">
           <option value='ascAtk'>Ascendente</option>
            <option value='descAtk'>Descendente</option>
           </select>

           <select placeholder="Filtrar pokemon">
            <option value='all'>TODOS</option>
            <option  value='created'>Creados</option>
            <option value='api'>Api</option>
           </select>

           <select placeholder="Filtrar por tipo">

           </select>
           <div>
           {
    allPokemons?.map(el=>{
        <div value>
        <Link to={"/home"+ el.id}>
        <Card name={el.name} type={el.type} img={el.img} value={el.id}/>
        </Link>
        </div>
    })
    
}
           </div>

           

        


    </div>
</div>
    )
}


// Botones/Opciones para filtrar por tipo de pokemon y por pokemon existente o creado por nosotros
// [ ] Botones/Opciones para ordenar tanto ascendentemente como descendentemente los pokemons por orden alfabético y por ataque
// [ ] Paginado para ir buscando y mostrando los siguientes pokemons, 12 pokemons por pagina.
