import React from "react";
import {Link} from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { pokeDetail } from "../actions/index";
import { useEffect } from "react";


export default function Detail(props){
    console.log("somos las props",props)
    const dispatch=useDispatch()

    useEffect(()=>{
        dispatch(pokeDetail(props.match.params.id));
    },[dispatch])

    const myPoke = useSelector((state)=>state.detail)
console.log("myPoke",myPoke)
console.log("mypoke.name:",myPoke.name)
    return(
        <div>
            {
            // myPoke.length>0 ?
            <div>
                <h1>{myPoke.name}</h1>
                <img src={myPoke.img}/>
                <h3>ID:{myPoke.id}</h3>
                <h4>Stats</h4>
                <h5>Vida:{myPoke.hp}</h5>
                <h5>Ataque:{myPoke.attack}</h5>
                <h5>Defensa:{myPoke.defense}</h5>
                <h5>Velocidad:{myPoke.speed}</h5>
                <h6>Altura:{myPoke.height}</h6>
                <h6>Peso:{myPoke.weight}</h6>
                <h6>Tipos:{myPoke.type? myPoke[0].type: myPoke[0].types}</h6>
                
            </div>
            
            // :
            // console.log("pedo")
            
            }
            <Link to='/home'>
                <button>Volver al Home</button>
            </Link>

        </div>
    )
}
