import React from "react";
import {useState,useEffect} from "react";
import {useDispatch,useSelector} from 'react-redux'
import { getPokemons,getTypes,filterPokeByType,filterPokeByCreated, sortByName,sortByATK } from "../actions";
import { Link } from "react-router-dom";
import Card from "./Card";
import Paginado from "./Paginado";
import SearchBar from "./SearchBar";
import "./Home.css";



export default function Home(){
    const dispatch= useDispatch()
    

    const allPokemons=useSelector((state)=>state.pokemons)
    useEffect(()=>{
        dispatch(getPokemons());
    },[dispatch])
    
    const allTypes=useSelector((state)=>state.types)
    useEffect(()=>{
        dispatch(getTypes());
    },[dispatch])
    const[orden,setOrden]=useState('')
    const[currentPage,setCurrentPage]=useState(1)
    const [pokePerPage, setPokePerPage]=useState(12)
    const indexOfLastPoke=currentPage * pokePerPage
    const indexOfFirstPoke=indexOfLastPoke - pokePerPage
    const currentPoke= allPokemons.slice(indexOfFirstPoke,indexOfLastPoke)

    const paginado=(pageNumber)=>{
        setCurrentPage(pageNumber)
    }


    function handleClick(e){
        e.preventDefault();
        dispatch(getPokemons());

    }

    function handleFilterType(e){
        dispatch(filterPokeByType(e.target.value));

    }

    function handlefilterPokeByCreated(e){
        dispatch(filterPokeByCreated(e.target.value))
    }

    function handleSortByName (e){
        e.preventDefault();
        dispatch(sortByName(e.target.value))
        setCurrentPage(1);
        setOrden(`Ordenado ${e.target.value}`)
    }
    function handleSortByATK (e){
        e.preventDefault();
        dispatch(sortByATK(e.target.value))
        setCurrentPage(1);
        setOrden(`Ordenado ${e.target.value}`)
    }

    

    return(
<div className="homeBox">
    
    
    <h1 className="titleHome">Pok√©mon Individual Proyect</h1>
    <div className="buttonDiv">
    <button onClick={e=>{handleClick(e)}}>Reload Pok√©mon</button>
    <button><Link to='/pokemon' className="buttonDiv">Create Pok√©mon</Link></button>
    </div>
    <div className="filters">
           <select onChange={e=> handleSortByName(e)}>
           <option value="" hidden>ABC</option>
            <option value='asc'>Increasing</option>
            <option value='desc'>Decreasing</option>

           </select>

           <select onChange={e=> handleSortByATK(e)}>
           <option value="" hidden>ATK</option>
           <option value='asc'>Increasing</option>
            <option value='desc'>Decreasing</option>
           </select>

           <select onChange={e=> handlefilterPokeByCreated(e)}>
           <option value="" hidden>Created</option>
            <option value='all'>All</option>
            <option  value='created'>DB</option>
            <option value='api'>Api</option>
           </select>

           <select class onChange={e=> handleFilterType(e)}>
           <option value="" hidden>Type</option>
            <option value='all' >All</option>
            {
                allTypes?.map(el=>(
                    <option key={el.id} value={el.name}>{el.name}</option>
                ))

            }
           </select>
           <SearchBar/>
           <div className="paginadoBar">
    <Paginado
           pokePerPage={pokePerPage}
            allPokemons={allPokemons.length}
            paginado={paginado}/>
    </div>
           
           <div>

         <div className="cards">
         {
    currentPoke?.map(el=>{
        return(
            <div className="Cards" key={el.id}>
            <Link to={"/detail/"+ el.id} key={el.id} className="linkDiv">
            <Card name={el.name} type={el.type} img={el.img} Types={el.Types} key={el.id}/>
            </Link>
            </div>
        );
       
    })}
         </div>
           
    
           </div>

           

        


    </div>
    
    
</div>

    )
}


// Botones/Opciones para filtrar por tipo de pokemon y por pokemon existente o creado por nosotros
// [ ] Botones/Opciones para ordenar tanto ascendentemente como descendentemente los pokemons por orden alfab√©tico y por ataque
// [ ] Paginado para ir buscando y mostrando los siguientes pokemons, 12 pokemons por pagina.
