const initialState={
    pokemons:[],
    allPokemons:[],
    types:[]
}

function rootReducer(state=initialState,action){
switch(action.type){
    case 'GET_POKEMONS':
        return{
            ...state,
            allPokemons:action.payload,
            pokemons:action.payload
        }

    case 'GET_TYPES':
        return{
            ...state,
            types:action.payload
        }

    case 'FILTER_BY_TYPE':
        const allPokemo=state.allPokemons
        const typeFiltered=action.payload==='all'? allPokemons :
        allPokemons.filter((el)=>el.type.includes(action.payload))
        return{
         ...state,
         pokemons:typeFiltered
         
            

        }
    default:
        return state;
}
}

