import React from "react";
import {Link} from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { pokeDetail } from "../actions/index";
import { useEffect } from "react";
import "./Detail.css";


export default function Detail(props){
    console.log("somos las props", props)
    const dispatch=useDispatch()

    useEffect(()=>{
        dispatch(pokeDetail(props.match.params.id));
    },[dispatch])

    const myPoke = useSelector((state)=>state.detail)
console.log("myPoke",myPoke)
console.log("mypoke.name:",myPoke.name)
    return(

        <div className="box">

<Link to='/home' className="Link">
                <button className="button">Volver al Home</button>
            </Link>
            {
            // myPoke.length>0 ?
            <div className="detailBox">
                <div className="primary">
                <h1 className="title">{myPoke.name}</h1>
                <h3>ID:{myPoke.id}</h3>
                <img src={myPoke.img} className="imgDetail"/>
                
                </div>
                
                <h4>Stats</h4>
                <div className="stats">
                <h5>Vida:{myPoke.hp}</h5>
                <h5>Ataque:{myPoke.attack}</h5>
                <h5>Defensa:{myPoke.defense}</h5>
                <h5>Velocidad:{myPoke.speed}</h5>
                <h5>Altura:{myPoke.height}</h5>
                <h5>W:{myPoke.weight}</h5>
               
                 <h4>Tipos:</h4>
                 <h5>{!myPoke.created? myPoke.type + ' ' :myPoke.Types.map(el=>el.name + (' '))}</h5>

                </div>
              
            </div>
            
            
           
            
            }
            

        </div>
    )
}
