import React, {useState,useEffect} from "react";
import { Link, useHistory } from "react-router-dom";
import{getTypes,createPoke} from '../actions/index'
import { useDispatch,useSelector } from "react-redux";


export default function PokeCreate(){
    const dispatch=useDispatch()
    const types=useSelector((state)=> state.types)

    const [input,setInput]=useState({
        name: "",
        hp: "",
        attack: "",
        defense: "",
        speed: "",
        height: "",
        weight: "",
        img: "",
        types:[]

    })

    useEffect(()=>{
        dispatch(getTypes());  
    },[]);

    return(
        <div>
            <Link to='/home'><button>Volver al Home</button></Link>
            <h1>Crea tu Pok√©mon</h1>
            <form>
                <div>
                    <label>Nombre:</label>
                    <input
                    type="text"
                    value={input.name}
                    name="name"/>

                    <label>Nombre:</label>
                    <input
                    type="text"
                    value={input.name}
                    name="name"/>

                    <label>Nombre:</label>
                    <input
                    type="text"
                    value={input.name}
                    name="name"/>

               <label>Nombre:</label>
                    <input
                    type="text"
                    value={input.name}
                    name="name"/>

               <label>Nombre:</label>
                    <input
                    type="text"
                    value={input.name}
                    name="name"/>

               <label>Nombre:</label>
                    <input
                    type="text"
                    value={input.name}
                    name="name"/>

               <label>Nombre:</label>
                    <input
                    type="text"
                    value={input.name}
                    name="name"/>
                    <label>Nombre:</label>
                    <input
                    type="text"
                    value={input.name}
                    name="name"/>
                </div>
            </form>
        </div>
    )
}
