import React, {useState,useEffect} from "react";
import { Link, useHistory } from "react-router-dom";
import{getTypes,createPoke} from '../actions/index'
import { useDispatch,useSelector } from "react-redux";
import "./PokeCreate.css"



function validate(input){
    let errors={};
    if(!input.img ){
        errors.img='Ingrese una imagen válida'
        console.log("length",input.types.length)
    }
    if(!input.name){
        errors.name='se requiere un Nombre válido';
    }else if( input.hp<=0 || input.hp>250){
        errors.hp='Ingrese un numero entre 1 y 250'
    }else if( input.attack <=0 || input.attack >250){
        errors.attack ='Ingrese un numero entre 1 y 250'
    }else if(input.defense<=0 || input.defense>250){
        errors.defense='Ingrese un numero entre 1 y 250'
    }else if(input.speed<=0 || input.speed>250){
        errors.speed='Ingrese un numero entre 1 y 250'
    }else if(input.height<=0 || input.height>250){
        errors.height='Ingrese un numero entre 1 y 250'
    }else if(input.weight<=0 || input.weight>250){
        errors.weight='Ingrese un numero entre 1 y 250'
    }else if(input.check==="0" || input.check>2){
        console.log("length",input.types.length)
        console.log("input.check",input.check)
        errors.check='Seleccione uno o dos tipos'
    }
    return errors;
}

export default function PokeCreate(){
    const dispatch=useDispatch()
    const types=useSelector((state)=> state.types)
    const history=useHistory()
    const [errors,setErrors]=useState({});
    const [input,setInput]=useState({
        name: "",
        hp: "",
        attack: "",
        defense: "",
        speed: "",
        height: "",
        weight: "",
        img: "",
        types:[],
        check:0

    })
function handleChange(e){
    setInput({
        ...input,
        [e.target.name]:e.target.value
    })
    setErrors(validate({
        ...input,
        [e.target.name]:e.target.value
    }))
    console.log("input",input)
}

function handleCheck(e){
    if(e.target.checked){
        
        setInput({
            ...input,
            types:[...input.types,e.target.value],
            check:input.check+1
        })
        console.log("check.length",input.types.length)
        console.log("handle input.check",input.check)
    }else if(!e.target.checked){
        setInput({
            ...input,
            types:input.types.filter(t=>t!==e.target.value),
            check:input.check-1
        })
        console.log("check.length",input.types.length)
        console.log("handle input.check",input.check)
    }
}


function handleSubmit(e){
    e.preventDefault();
    console.log("finishInput:",input)
    dispatch(createPoke(input))
    alert("El pokémon ha sido creado")
    setInput({
        name: "",
        hp: "",
        attack: "",
        defense: "",
        speed: "",
        height: "",
        weight: "",
        img: "",
        types:[]
    })
    // history.push('/home')
}




    useEffect(()=>{
        dispatch(getTypes());  
    },[dispatch]);

    return(
        <div className="createBox">
            <Link to='/home'><button>Volver al Home</button></Link>
            <h1 className="titleCreate">Crea tu Pokémon</h1>
            
            <form>
                <div className="containerDiv">
                <div>
            <div className="imageDivCreate">
            <img src={"https://assets.stickpng.com/images/580b57fcd9996e24bc43c325.png"} alt="img not found" width={"300px"} height={"300px"}/>
            </div>
            <div className="form2"></div>
                <div>
                    <label>Nombre:</label>
                    <input
                    type="text"
                    value={input.name}
                    name="name"
                    onChange={handleChange}/>
                    {errors.name && (
                        <p className='error'>{errors.name}</p>
                    )}
                </div>
                <div>
                    <label>Vida:</label>
                    <input
                    type="number"
                    value={input.hp}
                    name="hp"
                    onChange={handleChange}/>

                    {errors.hp && (
                        <p className='error'>{errors.hp}</p>
                    )}
                </div>
                <div>
                    <label>Ataque:</label>
                    <input
                    type="number"
                    value={input.attack}
                    name="attack"
                    onChange={handleChange}/>
                    {errors.attack && (
                        <p className='error'>{errors.attack}</p>
                    )}
                    
                </div>
                <div>
                    <label>Defensa:</label>
                    <input
                    type="number"
                    value={input.defense}
                    name="defense"
                    onChange={handleChange}/>
                    {errors.defense && (
                        <p className='error'>{errors.defense}</p>
                    )}
                    
                </div>
                <div>
                    <label>Velocidad:</label>
                    <input
                    type="number"
                    value={input.speed}
                    name="speed"
                    onChange={handleChange}/>
                    {errors.speed && (
                        <p className='error'>{errors.speed}</p>
                    )}
                    
                </div>
                <div>
                    <label>Altura:</label>
                    <input
                    type="number"
                    value={input.height}
                    name="height"
                    onChange={handleChange}/>
                    {errors.height && (
                        <p className='error'>{errors.height}</p>
                    )}
                    
                </div>
                <div>
                    <label>Peso:</label>
                    <input
                    type="number"
                    value={input.weight}
                    name="weight"
                    onChange={handleChange}/>
                    {errors.weight && (
                        <p className='error'>{errors.weight}</p>
                    )}
                    
                </div>
                <div>
                    <label>Imagen:</label>
                    <input
                    type="text"
                    value={input.img}
                    name="img"
                    onChange={handleChange}
                    />
                    </div>
                   {errors.img ? (
                    <div>
                        <p className='error'>{errors.img}</p>
                        <img src="https://imagenpng.com/wp-content/uploads/2016/09/Pokebola-pokeball-png-4.png" alt="img not found" width={"100px"} height={"100px"}/>
                        </div>
                    ):(
                    <div>
                        <br/>
                        <br/>
                        <ul>La imagen seleccionada es:<li><img src={input.img} alt="img not found" width={"100px"} height={"100px"}/></li></ul>
                    
                    </div>
                    )
                    }
                    
                </div>
                </div>

                
                <div className="allType">
                    <label>Seleccione tipo:</label>
                    {errors.check && (
                        <p className='error'>{errors.check}</p>
                    )}
                    <br/>
                    <div className="typeSelect">
                    {types.map((el)=>(
                        <label><input
                        type="checkbox"
                        name={el.name}
                        value={el.name}
                        onClick={(e)=>handleCheck(e)}/>
                        {el.name}


                        </label>
                    ))}
                    
                </div>
                </div>
                <div>
                    <button
                    type="submit"
                    onClick={(e)=>handleSubmit(e)}>
                        Crear Pokémon
                    </button>
                </div>
                <ul>El pokémon es tipo:
                    <li>{input.types.map(el=>el + " , ")}</li>
                </ul>
            </form>
        </div>
    )
}
