import React from "react";
import {useState,useEffect} from "react";
import {useDispatch,useSelector} from 'react-redux'
import { getPokemons,getTypes } from "../actions";
import { Link } from "react-router-dom";
import Card from "./Card";



export default function Home(){
    const dispatch= useDispatch()

    const allPokemons=useSelector((state)=>state.pokemons)
    useEffect(()=>{
        dispatch(getPokemons());
    },[dispatch])
    
    const allTypes=useSelector((state)=>state.types)
    console.log(allTypes)


    function handleClick(e){
        e.preventDefault();
        dispatch(getPokemons());

    }
    return(
<div>
    <Link to='/character'>Create Pokémon</Link>
    <h1>PI Pokémon</h1>
    <button onClick={e=>{handleClick(e)}}>
        Volver a cargar todos los pokémons
    </button>
    <div>
           <select >
           <option value="" diseabled selected hidden>Alfabético</option>
            <option value='ascABC'>Ascendente</option>
            <option value='descABC'>Descendente</option>

           </select>

           <select>
           <option value="" diseabled selected hidden>Ataque</option>
           <option value='ascAtk'>Ascendente</option>
            <option value='descAtk'>Descendente</option>
           </select>

           <select>
           <option value="" diseabled selected hidden>Creados</option>
            <option value='all'>TODOS</option>
            <option  value='created'>Creados</option>
            <option value='api'>Api</option>
           </select>

           <select>
           <option value="" diseabled selected hidden>Tipo</option>
            <option value='all'>TODOS</option>
            {

            }
           </select>
           <div>
           {
    allPokemons?.map(el=>{
        return(
            <div className="Cards">
            <Link to={"/home"+ el.id}>
            <Card name={el.name} type={el.type} img={el.img} key={el.id}/>
            </Link>
            </div>
        );
       
    })}
           </div>

           

        


    </div>
</div>
    )
}


// Botones/Opciones para filtrar por tipo de pokemon y por pokemon existente o creado por nosotros
// [ ] Botones/Opciones para ordenar tanto ascendentemente como descendentemente los pokemons por orden alfabético y por ataque
// [ ] Paginado para ir buscando y mostrando los siguientes pokemons, 12 pokemons por pagina.
