import React, {useState,useEffect} from "react";
import { Link, useHistory } from "react-router-dom";
import{getTypes,createPoke} from '../actions/index'
import { useDispatch,useSelector } from "react-redux";


export default function PokeCreate(){
    const dispatch=useDispatch()
    const types=useSelector((state)=> state.types)
    const history=useHistory()
    const [input,setInput]=useState({
        name: "",
        hp: "",
        attack: "",
        defense: "",
        speed: "",
        height: "",
        weight: "",
        img: "",
        types:[]

    })
function handleChange(e){
    setInput({
        ...input,
        [e.target.name]:e.target.value
    })
    console.log("input",input)
}

function handleCheck(e){
    if(e.target.checked){
        setInput({
            ...input,
            types:[...input.types,e.target.value]
        })
        console.log("inputCheck",input.types)
    }else if(!e.target.checked){
        setInput({
            ...input,
            types:input.types.filter(t=>t!==e.target.value)
        })
    }
}


function handleSubmit(e){
    e.preventDefault();
    console.log("finishInput:",input)
    dispatch(createPoke(input))
    alert("El pokÃ©mon ha sido creado")
    setInput({
        name: "",
        hp: "",
        attack: "",
        defense: "",
        speed: "",
        height: "",
        weight: "",
        img: "",
        types:[]
    })
    
}




    useEffect(()=>{
        dispatch(getTypes());  
    },[dispatch]);

    return(
        <div>
            <Link to='/home'><button>Volver al Home</button></Link>
            <h1>Crea tu PokÃ©mon</h1>
            <img src={"https://assets.stickpng.com/images/580b57fcd9996e24bc43c325.png"} alt="img not found" width={"300px"} height={"300px"}/>
            <form>
                <div>
                    <label>Nombre:</label>
                    <input
                    type="text"
                    value={input.name}
                    name="name"
                    onChange={handleChange}/>
                    
                </div>
                <div>
                    <label>Vida:</label>
                    <input
                    type="number"
                    value={input.hp}
                    name="hp"
                    onChange={handleChange}/>
                    
                </div>
                <div>
                    <label>Ataque:</label>
                    <input
                    type="number"
                    value={input.attack}
                    name="attack"
                    onChange={handleChange}/>
                    
                </div>
                <div>
                    <label>Defensa:</label>
                    <input
                    type="number"
                    value={input.defense}
                    name="defense"
                    onChange={handleChange}/>
                    
                </div>
                <div>
                    <label>Velocidad:</label>
                    <input
                    type="number"
                    value={input.speed}
                    name="speed"
                    onChange={handleChange}/>
                    
                </div>
                <div>
                    <label>Altura:</label>
                    <input
                    type="number"
                    value={input.height}
                    name="height"
                    onChange={handleChange}/>
                    
                </div>
                <div>
                    <label>Peso:</label>
                    <input
                    type="number"
                    value={input.weight}
                    name="weight"
                    onChange={handleChange}/>
                    
                </div>
                <div>
                    <label>Imagen:</label>
                    <input
                    type="text"
                    value={input.img}
                    name="img"
                    onChange={handleChange}
                    />
                   
                    
                </div>

                <div>
                    <br/>
                    <br/>
                    <ul>La imagen seleccionada es:<li><img src={input.img} alt="img not found" width={"100px"} height={"100px"}/></li></ul>
                
                </div>
                <div>
                    <label>Seleccione tipo:</label>
                    <br/>
                    {types.map((el)=>(
                        <label><input
                        type="checkbox"
                        name={el.name}
                        value={el.name}
                        onChange={(e)=>handleCheck(e)}/>
                        {el.name}


                        </label>
                    ))}
                </div>
                <div>
                    <button
                    type="submit">Crear PokÃ©mon</button>
                </div>
                <ul>El pokÃ©mon es tipo:
                    <li>{input.types.map(el=>el + " , ")}</li>
                </ul>
            </form>
        </div>
    )
}
